import React, { useState } from "react";
import Icon from "./Icon";

const PopupContext = React.createContext();

export const Popup = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="popup">
      <PopupContext.provider values={{ isOpen, setIsOpen }}>
        {props.children}
      </PopupContext.provider>
    </div>
  );
};

const Toggle = () => {
  const { isOpen, setIsOpen } = useContext(PopupContext);

  return (
    <div className="popup-btn" onClick={() => setIsOpen(!isOpen)}>
      <Icon />
    </div>
  );
};

const List = ({ children }) => {
  const { open } = useContext(PopupContext);
  return open && <ul className="popup-list">{children}</ul>;
};

const Item = ({ children }) => {
  return <li className="popup-item">{children}</li>;
};

Popup.Toggle = Toggle;
Popup.List = List;
Popup.Item = Item;
